mapper.insert:
INSERT INTO DL_REVIEW ( ORDER_NUM
                      , STORE_ID
                      , REVIEW_CONTENT
                      , USER_ID, SCORE
                      , REVIEW_IMG)
VALUES ( ${orderNum}
       , #{storeId}
       , #{reviewContent}
       , #{userId}
       , #{score}
       , #{reviewImg});

mapper.update:
UPDATE DL_REVIEW SET
    REVIEW_CONTENT = #{reviewContent}
    , SCORE = #{score}

    , REVIEW_IMG = #{reviewImg}

WHERE
    ORDER_NUM = ${orderNum}
